mapper.select:
SELECT book_id AS bookId, title, author, book_category AS bookCategory, book_status AS bookStatus, created_date AS createdDate, last_modified_date AS lastModifiedDate FROM book
and title like concat('%', #{searchTitle}, '%')
and author like concat('%', #{searchAuthor}, '%')
and book_category like concat('%', #{searchCategory}, '%')
and book_status = #{bookStatus}
ORDER BY title ASC LIMIT #{limit} OFFSET #{offset}
